package com.taoxiha.common.utils;import java.util.List;import java.util.Map;import java.util.TreeMap;import org.springframework.util.CollectionUtils;public class CollectionUtil extends CollectionUtils{	public static String mapToStr(Map<String,Object> params){    	if(null==params || params.isEmpty()){    		return null;    	}    	    	StringBuffer buffer= new StringBuffer();    	for(String str:params.keySet()){    		buffer.append(params.get(str)).append("|");    	}    	String str=buffer.toString();		return str.substring(0, str.length()-1);    	    }			public static String listToStr(List<String> params){		if(null==params || params.isEmpty()){    		return null;    	}    	StringBuffer buffer= new StringBuffer();    	for(String str:params){    		buffer.append(str).append("|");    	}    	String str=buffer.toString();		return str.substring(0, str.length()-1);	}}