package com.taoxiha.common.utils.security;import java.util.ResourceBundle;public class ConfigDesUtils {	private static Object lock              = new Object();	private static ConfigDesUtils config     = null;	private static ResourceBundle rb        = null;	private static final String CONFIG_FILE = "conf/desrandom";		private ConfigDesUtils() {		rb = ResourceBundle.getBundle(CONFIG_FILE);	}		public static ConfigDesUtils getInstance() {		synchronized(lock) {			if(null == config) {				config = new ConfigDesUtils();			}		}		return (config);	}		public String getValue(String key) {		return (rb.getString(key));	}}