package com.taoxiha.service.crawl.entity;import java.io.Serializable;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import com.taoxiha.common.utils.StringUtils;import com.taoxiha.common.utils.date.DateUtils;import com.taoxiha.common.utils.json.JacksonMapper;import com.taoxiha.service.crawl.entity.Cookie;/** * 请求参数 cookie 和 其他参数集合* @ClassName: CrawlReqParams * @Description: 参数封装* @author zguowei jakbb01@gmail.com* @date 2013-6-11 上午2:17:11 * */public class ReqParams implements Serializable{		private static final long serialVersionUID = 1L;	private List<Cookie> cookies;		private Map<String,String> reqParams;			public ReqParams (){}		public static ReqParams parserParams(String params){		if(StringUtils.isBlank(params)){			return null;		}		return JacksonMapper.readValue(params, ReqParams.class);	}	public List<Cookie> getCookies() {		return cookies;	}	public void setCookies(List<Cookie> cookies) {		this.cookies = cookies;	}	public Map<String, String> getReqParams() {		return reqParams;	}	public void setReqParams(Map<String, String> reqParams) {		this.reqParams = reqParams;	}		public static void main(String[] args) {		ReqParams reqParams= new ReqParams();		List<Cookie> cookies= new ArrayList<Cookie>();		 Map<String,String> params = new HashMap<String,String>();		for(int i=0;i<5;i++){			Cookie cookie=new Cookie("taoxiha.com", "userName"+i, "jakbb"+i, "/", DateUtils.getUnixTime(), true);			cookies.add(cookie);		 params.put("key"+i, "value"+i);			}				reqParams.setCookies(cookies);		reqParams.setReqParams(params);		String json=JacksonMapper.getJsonFromEntity(reqParams);		System.out.println(json);		reqParams=reqParams.parserParams(json);		System.out.println(reqParams.getReqParams().get("key1"));	}}