package com.taoxiha.service.exception;public class ServiceException extends Exception {	private static final long serialVersionUID = 1L;		private int code;	private String msg;	private Error error;		public static enum Error{		NULL_ERROR(00,"value is null"),ACTION_ERROR(01,"执行错误");		private int code;		private String msg;				private Error(int code, String msg) {			this.code = code;			this.msg = msg;		}		public int getCode() {			return code;		}		public void setCode(int code) {			this.code = code;		}		public String getMsg() {			return msg;		}		public void setMsg(String msg) {			this.msg = msg;		}			}		public ServiceException(){}		public ServiceException(Error error) {		super(error.getMsg());		this.error = error;	}	public Error getError() {		return error;	}	public int getCode() {		return code;	}	public void setCode(int code) {		this.code = code;	}		public String getMsg() {		return msg;	}	public void setMsg(String msg) {		this.msg = msg;	}	public ServiceException(int code, String msg) {		super(msg);		this.code = code;		this.msg = msg;	}		public ServiceException(String message) {		super(message);		this.code=0;		this.msg=message;	}	public ServiceException(Throwable cause) {		super(cause);	}	public ServiceException(String message, Throwable cause) {		super(message, cause);	}	}