package test.crawl.examples;public class Utils {		/**     * 去除掉Html标签及其他标点符号    * @Title: replaceHtmlTag     * @Description: TODO    * @Author zguowei jakbb01@gmail.com    * @Time 2013-5-28 下午3:39:58    * @param str    * @return    * @return String    * @throws     */    public static String replaceHtmlTag(String str){    	if(null == str || "".equals(str) || 0 == str.length()){    		return null;    	}    	  str = str.replaceAll("\\&[a-zA-Z]{1,10};", "").replaceAll(                  "<[^>]*>", "");          str = str.replaceAll("[!@#$%^&*()_+=|/\\?/><:\"',.`~￥!{}\\[\\] ]", "");         return str;    }	}